$(document).ready(function () {
	$('.news-holder').slick({
		centerPadding: '0',
		slidesToShow: 3,
		swipe: false,
		responsive: [
		{
			breakpoint: 992,
			settings: {
				slidesToShow: 3
			}
		},
		{
			breakpoint: 768,
			settings: {
				slidesToShow: 2
			}
		},
		{
			breakpoint: 480,
			settings: {
				slidesToShow: 1
			}
		}
		]
	});
	$('.stock-holder').slick({
		centerPadding: '0',
		slidesToShow: 4,
		swipe: false,
		responsive: [
		{
			breakpoint: 992,
			settings: {
				slidesToShow: 3
			}
		},
		{
			breakpoint: 768,
			settings: {
				slidesToShow: 2
			}
		},
		{
			breakpoint: 480,
			settings: {
				slidesToShow: 1
			}
		}
		]
	});

	$(".opener").click(function(){
		$(this).closest("header").toggleClass("active");
		/*$(document).mouseup(function (e) {
			var container = $(".drop");
			if (container.has(e.target).length === 0){
				$(this).closest("header").toggleClass("active");
				
			}
		});*/
	});

	$(".dropdown-menu li").click(function(){
		$(this).addClass("current");
		$(this).closest(".dropdown").find('[data-toggle=dropdown]').html($(this).text() + "<b class='caret'></b>");
	});

	
});


$(window).load(function () {
	sameHeight(".home-news .item");
	sameHeight(".home-stock .item");
});


function sameHeight(selector){
	var maxheight = 0;
	$(selector).each(function() {
		if($(this).height() > maxheight) { maxheight = $(this).height(); }
	});

	$(selector).height(maxheight);
}